write-output "Virtual Machine IP address : $IP" write-output "Using JAVA_HOME : $env:JAVA_HOME" write-output "Using ADFSAutomation_HOME : $env:ADFSAutomation_HOME" write-output "Using ADFSAutomation_CLASS_PATH : $ADFSAutomation_CLASS_PATH"
$env:_RUNJAVA="$env:JAVA_HOME\bin\java" & "$env:_RUNJAVA" "$env:JAVA_OPTS" ‘-cp’ "$ADFSAutomation_CLASS_PATH" com.igate.adfsauto.client.AdfsAutoWSClient $vcoServerIP $vcoServerPort $vcoAdminGrpUsername $vcoAdminGrpPasswd $workFlowName $resourceADFSvmIP $resourceADFSvmUsr $resourceADFSvmPassd $resCertThumbprint $claimproviderIPAddr $claimproviderHostName $claimproviderCertpath $claimProRuleName $debugFlag
