ns7:component-version-script.script:
#!/bin/bash # Copyright (c) 2013 Trymaran, Inc. All rights reserved.
. $global_conf set -e
test "$ENABLED" != "true" && exit 0;
# Establish current timestamp timestamp=`date +%Y-%m-%d-%H-%M`
# Establish working directory WORKING_DIR="/tmp" test -n "$TMP_DIR" && WORKING_DIR=$TMP_DIR
# Unzip the installer files INSTALLER_DIR="$WORKING_DIR/rcu" test -d "$INSTALLER_DIR" || mkdir $INSTALLER_DIR
unzip -q $RCU_INSTALL_ZIP -d $INSTALLER_DIR chown -R $OS_USER $INSTALLER_DIR chmod -R u+w $INSTALLER_DIR
# Populate password file PASSWORD_FILE=$INSTALLER_DIR/passfile.txt echo $DB_PASSWORD > $PASSWORD_FILE chown $OS_USER $PASSWORD_FILE
# Populate run file RUN_FILE=$INSTALLER_DIR/runrcu.sh echo '#!/bin/sh' > $RUN_FILE chown $OS_USER $RUN_FILE chmod 755 $RUN_FILE
# Prepare component arguments COMPONENT_ARGS="" for i in ${!COMPONENTS[*]} do COMPONENT_ARGS+="-component ${COMPONENTS[$i]} " test -n "$TABLESPACE" && COMPONENT_ARGS+="-tablespace $TABLESPACE " test -n "$TEMP_TABLESPACE" && COMPONENT_ARGS+="-tempTablespace $TEMP_TABLESPACE " echo $PASSWORD >> $PASSWORD_FILE done;
# Run RCU echo $INSTALLER_DIR/rcuHome/bin/rcu -silent -createRepository -databaseType $DB_TYPE -connectString $DB_CONNECT_STRING \ -dbUser $DB_USER -dbRole $DB_ROLE -schemaPrefix $PREFIX $COMPONENT_ARGS \ -useSamePasswordForAllSchemaUsers -f '<' $PASSWORD_FILE >> $RUN_FILE
if [ "$OS_USER" = "root" ]; then $RUN_FILE else su $OS_USER -c "$RUN_FILE" fi;
# Clean up rm -rf $PASSWORD_FILE $RUN_FILE $INSTALLER_DIR if [ "$DELETE_CONTENT" = "true" ]; then rm -f $RCU_INSTALL_ZIP fi;

supported-os-details.exec-script:
trap "APPDEXITSTATUS=\$?;printenv > $2;exit $APPDEXITSTATUS" EXIT #Start of Json Parser
#result of getValueFromJson is saved in global variable $JSONVALUE JSONVALUE=""
#use 2 arrays to simulate associate array in Bash 3.x declare -a pathArray declare -a valueArray
function _appd_pathMap_add () { local path local value path=$1 value=$2 pathArray=("${pathArray[@]}" "$path") valueArray=("${valueArray[@]}" "$value") }
function _appd_pathMap_init () { unset pathArray unset valueArray declare -a pathArray declare -a valueArray }
function _appd_pathMap_get () { local path=$1 local length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do if [ "${pathArray[$i]}" == "$path" ]; then echo "${valueArray[$i]}"; return; fi done; _appd_throw_error "Key path $path not found" }
function _appd_pathMap_output () { local length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do echo "${pathArray[$i]} values ${valueArray[$i]}" done; }
function _appd_throw_error () { echo "$*" >&2 exit 1 }
function _appd_awk_egrep () { local pattern_string=$1
gawk '{ while ($0) { start=match($0, pattern); token=substr($0, start, RLENGTH); print token; $0=substr($0, start+RLENGTH); } }' pattern=$pattern_string }
function _appd_tokenize () { local GREP local ESCAPE local CHAR
if echo "test string" | egrep -ao --color=never "test" &>/dev/null then GREP='egrep -ao --color=never' else GREP='egrep -ao' fi
if echo "test string" | egrep -o "test" &>/dev/null then ESCAPE='(\\[^u[:cntrl:]]|\\u[0-9a-fA-F]{4})' CHAR='[^[:cntrl:]"\\]' else GREP=_appd_awk_egrep ESCAPE='(\\\\[^u[:cntrl:]]|\\u[0-9a-fA-F]{4})' CHAR='[^[:cntrl:]"\\\\]' fi
local STRING="\"$CHAR*($ESCAPE$CHAR*)*\"" local NUMBER='-?(0|[1-9][0-9]*)([.][0-9]*)?([eE][+-]?[0-9]*)?' local KEYWORD='null|false|true' local SPACE='[[:space:]]+' $GREP "$STRING|$NUMBER|$KEYWORD|$SPACE|." | egrep -v "^$SPACE$" }
function _appd_parse_array () { local index=0 local ary='' read -r token case "$token" in ']');; *) while : do _appd_parse_value "$1" "[$index]" index=$((index+1)) ary="$ary""$value" read -r token case "$token" in ']') break ;; ',') ary="$ary," ;; *) _appd_throw_error "EXPECTED , or ] GOT ${token:-EOF}" ;; esac read -r token done esac value=`printf '[%s]' "$ary"` : }
function _appd_parse_object () { local key local obj='' read -r token case "$token" in '}') ;; *) while : do case "$token" in '"'*'"') key=$token ;; *) _appd_throw_error "EXPECTED string GOT ${token:-EOF}" ;; esac read -r token case "$token" in ':') ;; *) _appd_throw_error "EXPECTED : GOT ${token:-EOF}" ;; esac read -r token _appd_parse_value "$1" "${key:1:$((${#key}-2))}" obj="$obj$key:$value" read -r token case "$token" in '}') break ;; ',') obj="$obj," ;; *) _appd_throw_error "EXPECTED , or } GOT ${token:-EOF}" ;; esac read -r token done esac value=`printf '{%s}' "$obj"` : }
function _appd_parse_value () { local jpath [[ "$2" == \[* ]] && jpath="${1:+$1}$2" || jpath="${1:+$1.}$2" case "$token" in '{') _appd_parse_object "$jpath" ;; '[') _appd_parse_array "$jpath" ;; # At this point, the only valid single-character tokens are digits. ''|[!0-9]) _appd_throw_error "EXPECTED value GOT ${token:-EOF}" ;; *) value=$token ;; esac [ "$value" = '' ] && return _appd_pathMap_add "$jpath" "$value" : }
function _appd_parse () { read -r token _appd_parse_value read -r token case "$token" in '') ;; *) _appd_throw_error "EXPECTED EOF GOT $token" ;; esac }
function _appd_convert_to_script_string () { case `echo "$1" | tr A-Z a-z` in "true") echo true ;; "false") echo false ;; "null") echo "" ;; \"*\") echo "${1:1:$((${#1}-2))}" ;; *) echo "$1" ;; esac }
# # This method will parse JSON String, get value of given JSON keypath. # The result will saved in variable $JSONVALUE # The returned value type will be: # String: whenever (1) the value of given JSON keypath is a JSON string (enclosed by double quotation) # or (2) the value of given JSON keypath is a JSON object (enclosed by brace) # Number: whenever the value of given JSON keypath is a number # Keyword: whenever the value of given JSON keypath is true/false/null # Array: whenever the value of given JSON keypath is a JSON array (enclosed by square) # # This method will automatically remove enclosing double quotation when return String or Array of String # # Example: # getValueFromJson '{"key1":"value1", "key2":{"subkey1":"subValue1"}}' 'key2.subkey1' # $JSONVALUE will be "subValue1" # Example: # getValueFromJson '{"key1":["value1","value2"]}' 'key1[1]' # $JSONVALUE will be "value2" # Example: # getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '[1].key1' # $JSONVALUE will be "value2" # Example: # getValueFromJson '[{"key1":"value1"}, {"key1":"value2"}]' '' # $JSONVALUE will be an array with 2 elements. # first element is '{"key1":"value1"}' # second element is '{"key1":"value2"}' # Example: # getValueFromJson '{"key1":"value1"}' '' # $JSONVALUE will be same as input string. # function getValueFromJson () { local JSONSTRING=$1 local JSONPATH=$2 local tokenTmp local stringValue
_appd_pathMap_init tokenTmp=`mktemp` echo "$JSONSTRING" | _appd_tokenize > $tokenTmp _appd_parse < $tokenTmp rm -rf tokenTmp stringValue=`_appd_pathMap_get $JSONPATH` if [[ $stringValue == \[* ]] ; then local length JSONVALUE=() length=$((${#pathArray[@]}-1)) for i in `eval echo "{0..$length}"`; do if [[ "${pathArray[$i]}" =~ "$JSONPATH" ]]; then local subPath=${pathArray[$i]:${#JSONPATH}} if [[ "$subPath" =~ ^\[[0-9]+\]$ ]]; then local elementValue elementValue=`_appd_convert_to_script_string "${valueArray[i]}"` JSONVALUE=("${JSONVALUE[@]}" "$elementValue") fi fi done else JSONVALUE=`_appd_convert_to_script_string "$stringValue"` fi } #End of Json Parser
# Execute action script source $1 APPDRETURNSTATUS=$? # Unset trap trap - EXIT # Save env variables in output properties file printenv > $2 exit $APPDRETURNSTATUS

ns7:component-version-script.script:
#!/bin/bash
# Copyright (c) 2013 Trymaran, Inc. All rights reserved.

. $global_conf
set -e
set -x

export http_proxy=http://10.99.133.128:3128
export https_proxy=https://10.99.133.128:3128
export ftp_proxy=ftp://10.99.133.128:3128


yum install -y expect

# Establish working directory
WORKING_DIR="/tmp"
test -n "$TMP_DIR" && WORKING_DIR=$TMP_DIR

EXP_SCRIPT="$WORKING_DIR/create_soa_domain.exp"
cat > $EXP_SCRIPT << "EndOfScript"
#!/usr/bin/expect
# Copyright (c) 2013 Trymaran Inc. All rights reserved.

proc usage {} {
	puts "Usage: create_soa_domain.exp -soaOH <soa_oracle_home> -domainName <domain_name> -domainDir <domain_dir> -appDir <app_dir> -domainPassword <password> -domainPort <port> -dbPrefix <prefix> -dbHost <db_host> -dbPort <db_port> -dbSid <db_sid> -dbPassword <db_password> -sslEnabled <true/false> -sslPort <ssl_port> -bpm <true/false> -osb <true/false>"
	puts "  e.g. create_soa_domain.exp -soaOH /oracle/middleware/Oracle_SOA1 -domainName soa_domain -domainDir /oracle/middleware/user_projects/domains -appDir /oracle/middleware/user_projects/applications -domainPassword welcome1 -domainPort 7001 -dbPrefix DEV -dbHost dbhost.example.com -dbPort 1521 -dbSid exampledb -dbPassword welcome1 -sslEnabled true -sslPort 7002 -bpm true -osb true"
}

set soa_oh ""
set domain_name "soa_domain"
set domain_dir  ""
set app_dir ""
set port 7001
set password ""
set prefix "DEV"
set db_host ""
set db_port 1521
set db_sid ""
set db_password ""
set ssl_enabled "false"
set ssl_port 7002
set bpm "true"
set osb "true"

set argi 0
while {$argi < [llength $argv]} {
	set arg [lindex $argv $argi]

	if {$arg == "-domainName"} {
		incr argi
		set domain_name [lindex $argv $argi]
	} elseif {$arg == "-domainDir"} {
		incr argi
		set domain_dir [lindex $argv $argi]
	} elseif {$arg == "-appDir"} {
		incr argi
		set app_dir [lindex $argv $argi]
	} elseif {$arg == "-domainPassword"} {
		incr argi
		set password [lindex $argv $argi]
	} elseif {$arg == "-domainPort"} {
		incr argi
		set port [lindex $argv $argi]
	} elseif {$arg == "-dbPrefix"} {
		incr argi
		set prefix [lindex $argv $argi]
	} elseif {$arg == "-dbHost" } {
		incr argi
		set db_host [lindex $argv $argi]
	} elseif {$arg == "-dbPort" } {
		incr argi
		set db_port [lindex $argv $argi]
	} elseif {$arg == "-dbSid"} {
		incr argi
		set db_sid [lindex $argv $argi]
	} elseif {$arg == "-dbPassword"} {
		incr argi
		set db_password [lindex $argv $argi]
	} elseif {$arg == "-sslEnabled"} {
		incr argi
		set ssl_enabled [string tolower [lindex $argv $argi]]
	} elseif {$arg == "-sslPort"} {
		incr argi
		set ssl_port [lindex $argv $argi]
	} elseif {$arg == "-soaOH"} {
		incr argi
		set soa_oh [lindex $argv $argi]
	} elseif {$arg == "-bpm"} {
		incr argi
		set bpm [string tolower [lindex $argv $argi]]
	} elseif {$arg == "-osb"} {
		incr argi
		set osb [string tolower [lindex $argv $argi]]
	}

	incr argi;
}

puts "SOA Oracle Home = $soa_oh"
puts "Domain name = $domain_name"
puts "Domain dir = $domain_dir"
puts "Apps dir = $app_dir"
puts "Domain port = $port"
# puts "Domain password = $password"
puts "DB prefix = $prefix"
puts "DB host = $db_host"
puts "DB port = $db_port"
puts "DB SID = $db_sid"
# puts "DB password = $db_password"
puts "SSL enabled = $ssl_enabled"
if {$ssl_enabled == "true"} {
	puts "SSL port = $ssl_port"
}
puts "BPM included = $bpm"
puts "OSB included = $osb"

set i 0
set j 0
set k 0
set k2 0
set l 0
set m 0; set mm 0
set n 0
set p 0
set ds 1

# proc sl {} {sleep 5}

if {[file isdirectory $soa_oh] == 0} {
	puts "ERROR: Missing or Invalid SOA Oracle Home";
	usage;
	exit -1;
}

if { $domain_name == "" || $domain_dir == "" || $password == "" } {
	puts "ERROR: Invalid domain name and/or domain dir and/or domain password"
	usage;
	exit -1;
}

if { $prefix == "" || $db_host == "" || $db_port == "" || $db_sid == "" || $db_password == "" } {
	puts "ERROR: Invalid database prefix, host, port, sid, or password"
	usage;
	exit -1;
}

# TODO: Validate database connection?

set timeout 1200
if {[info vars tty_spawn_id] == ""} {
	# /dev/tty doesn't exist
	puts "INFO: Setting terminal type since tty doesn't exist"
	set stty_init "cooked rows 50 columns 130"
}

spawn $soa_oh/common/bin/config.sh -mode=console

expect_before {
	"CFGFWK-" {
		puts "ERROR: Spawned process errored out.";
		exit -1;
	}

	timeout {
		puts "ERROR: Spawned process timed out.";
		exit -1;
	}
}

expect {
	-re "Welcome:.*Enter.*>" {
		send "1\r";
		exp_continue;
	}

	-re "Select Domain Source:.*Enter.*>" {
		send "1\r"; 
		exp_continue;
	}

	-re "Application Template Selection:.*Enter.*>" {
		set tmpl_prompt "$expect_out(0,string)"

		if {$j == 0} {
			set res1 [regexp {Oracle SOA Suite for developers [^|]+\[([0-9]+)\]} $tmpl_prompt ma sub1]
			send "$sub1\r";
		} elseif {$j == 1} {
			set res1 [regexp {Oracle Enterprise Manager [^|]+\[([0-9]+)\]} $tmpl_prompt ma sub1]
			send "$sub1\r";
		} elseif {$j == 2 && $bpm == "true"} {
			set res1 [regexp {Oracle BPM Suite for developers [^|]+\[([0-9]+)\]} $tmpl_prompt ma sub1]
			send "$sub1\r";
		} elseif {($j == 2 || $j == 3) && $osb == "true"} {
			set res1 [regexp {Oracle Service Bus for developers [^|]+\[([0-9]+)\]} $tmpl_prompt ma sub1]
			send "$sub1\r";
			set j 3;
		} else {
			send "Next\r";
		}

		incr j;
		exp_continue;
	}

	-re "Edit Domain Information:.*Enter.*>" {
		send [lindex [list "$domain_name\r" "Next\r"] $i];
		incr i;
		exp_continue;
	}

	-re "Select the target domain directory.*Enter.*>" {
		send [lindex [list "$domain_dir\r" "Next\r"] $k];
		incr k;
		exp_continue;
	}

	-re "Select the applications directory.*Enter.*>" {
		if {$app_dir == ""} {
			send "Next\r";
		} else {
			send [lindex [list "$app_dir\r" "Next\r"] $k2];
			incr k2;
		}
		exp_continue;
	}

	-re "Configure Administrator User Name.*Enter.*>" {
		send [lindex [list "2\r" "$password\r" "3\r" "$password\r" "Next\r"] $l];
		incr l;
		exp_continue;
	}

	-re "Domain Mode Configuration:.*Enter.*>" {
		send "Next\r";
		exp_continue;
	}

	-re "Java SDK Selection:.*Enter.*>" {
		send "Next\r";
		exp_continue;
	}

	-re "Configure JDBC Data Sources:.*Enter.*>" {
		set ds_count 7;
		if {$osb == "true"} {set ds_count 8};

		if {$m == 0} {
			send "1\r"; incr m;
		} elseif {$m == 1 && $ds <= $ds_count} {
			send "$ds\r"; incr m;
		} elseif {$m == 1 && $ds > $ds_count} {
			send "Next\r";
		} else {
			if {$m == 2 && ![info exists db_names($ds)]} {
				set jdbc_prompt "$expect_out(0,string)"
				set res1 [regexp {1[| ]+\*Name:[| ]+([^| ]*)} $jdbc_prompt ma sub1]

				if {$res1 == 0} {
					puts "ERROR: Couldn't match data source name/value"
					exit -1;
				}

				set db_names($ds) $sub1;
				if {$db_names($ds) ne "wlsbjmsrpDataSource"} {set m 6}
			}

			if {$m == 2} {
				send "2\r"; incr m;
			} elseif {$m == 3} {
				send "18\r"; incr m;
			} elseif {$m == 4} {
				send "3\r"; incr m;
			} elseif {$m == 5} {
				send "3\r"; incr m;
			} elseif {$m == 6} {
				send "4\r"; incr m;
			} elseif {$m == 7} {
				send "$db_sid\r"; incr m;
			} elseif {$m == 8} {
				send "5\r"; incr m;
			} elseif {$m == 9} {
				send "$db_host\r"; incr m;
			} elseif {$m == 10} {
				send "6\r"; incr m;
			} elseif {$m == 11} {
				send "$db_port\r"; incr m;
			} elseif {$m == 12} {
				send "7\r"; incr m;
			} elseif {$m == 13} {
				switch $db_names($ds) {
					"SOADataSource" {set schema_name "SOAINFRA"}
					"EDNLocalTxDataSource" {set schema_name "SOAINFRA"}
					"mds-soa" {set schema_name "MDS"}
					"mds-owsm" {set schema_name "MDS"}
					"EDNDataSource" {set schema_name "SOAINFRA"}
					"SOALocalTxDataSource" {set schema_name "SOAINFRA"}
					"OraSDPMDataSource" {set schema_name "ORASDPM"}
					"wlsbjmsrpDataSource" {set schema_name "SOAINFRA"}
					default {
						puts "ERROR: Found unknown data source with name $db_names($ds)";
						exit -1;
					}
				}

				send "${prefix}_${schema_name}\r"; incr m;
			} elseif {$m == 14} {
				send "8\r"; incr m;
			} elseif {$m == 15} {
				send "$db_password\r"; incr m;
			} elseif {$m == 16} {
				send "9\r"; incr m;
			} elseif {$m == 17} {
				send "$db_password\r"; incr m;
			} elseif {$m == 18} {
				send "Accept\r";
				incr ds; set m 0;
			}
		}
		exp_continue;
	}

	-re "Run Database Scripts:.*Enter.*>" {
		send "Next\r";
		exp_continue;
	}

	-re "Select Optional Configuration:.*Enter.*>" {
		send [lindex [list "1\r" "Next\r"] $n];
		incr n;
		exp_continue;
	}

	-re "Configure the Administration Server:.*Enter.*>" {
		if {$ssl_enabled == "true"} {
			send [lindex [list "3\r" "$port\r" "4\r" "1\r" "4\r" "$ssl_port\r" "Next\r"] $p];
		} else {
			send [lindex [list "3\r" "$port\r" "Next\r"] $p];
		}

		incr p;
		exp_continue;
	}

}

EndOfScript

RUN_FILE="$WORKING_DIR/create_soa_domain.sh"
echo '#!/bin/sh' > $RUN_FILE
chown $OS_USER $EXP_SCRIPT $RUN_FILE
chmod 755 $EXP_SCRIPT $RUN_FILE

# Compute SSL args
SSL_ARGS="-sslEnabled false "
test -n "$ASERVER_SSL_PORT" && SSL_ARGS="-sslEnabled true -sslPort $ASERVER_SSL_PORT "

# Compute APP_DIR args
test -n "$APP_DIR" && APP_DIR_ARGS="-appDir $APP_DIR"

# Run create_soa_domain
echo cd $WORKING_DIR >> $RUN_FILE
echo $EXP_SCRIPT -soaOH "$MW_HOME/$SOA_ORACLE_HOME" -domainName "$DOMAIN_NAME" -domainDir "$DOMAIN_DIR" \
	-domainPassword "$DOMAIN_PASSWORD" -domainPort "$ASERVER_PORT" -bpm "$INCLUDE_BPM" -osb "$INCLUDE_OSB" $SSL_ARGS $APP_DIR_ARGS \
	-dbPrefix "$DB_PREFIX" -dbHost "$DB_HOST" -dbPort "$DB_PORT" -dbSid "$DB_SID" -dbPassword "$DB_PASSWORD" >> $RUN_FILE

if [ "$OS_USER" = "root" ]; then
	$RUN_FILE
else
	su $OS_USER -c $RUN_FILE
fi;

# Clean up
rm -f $EXP_SCRIPT $RUN_FILE

supported-os-details.exec-script:
trap "APPDEXITSTATUS=\$?;printenv > $2;exit $APPDEXITSTATUS" EXIT #Start of Json Parser
