#!/usr/bin/expect
# Copyright (c) 2013 Trymaran Inc. All rights reserved.

proc usage {} {
	puts "Usage: create_soa_domain.exp -soaOH <soa_oracle_home> -domainName <domain_name> -domainDir <domain_dir> -appDir <app_dir> -domainPassword <password> -domainPort <port> -dbPrefix <prefix> -dbHost <db_host> -dbPort <db_port> -dbSid <db_sid> -dbPassword <db_password> -sslEnabled <true/false> -sslPort <ssl_port> -bpm <true/false> -osb <true/false>"
	puts "  e.g. create_soa_domain.exp -soaOH /oracle/middleware/Oracle_SOA1 -domainName soa_domain -domainDir /oracle/middleware/user_projects/domains -appDir /oracle/middleware/user_projects/applications -domainPassword welcome1 -domainPort 7001 -dbPrefix DEV -dbHost dbhost.example.com -dbPort 1521 -dbSid exampledb -dbPassword welcome1 -sslEnabled true -sslPort 7002 -bpm true -osb true"
}

set soa_oh ""
set domain_name "soa_domain"
set domain_dir  ""
set app_dir ""
set port 7001
set password ""
set prefix "DEV"
set db_host ""
set db_port 1521
set db_sid ""
set db_password ""
set ssl_enabled "false"
set ssl_port 7002
set bpm "true"
set osb "true"

set argi 0
while {$argi < [llength $argv]} {
	set arg [lindex $argv $argi]

	if {$arg == "-domainName"} {
		incr argi
		set domain_name [lindex $argv $argi]
	} elseif {$arg == "-domainDir"} {
		incr argi
		set domain_dir [lindex $argv $argi]
	} elseif {$arg == "-appDir"} {
		incr argi
		set app_dir [lindex $argv $argi]
	} elseif {$arg == "-domainPassword"} {
		incr argi
		set password [lindex $argv $argi]
	} elseif {$arg == "-domainPort"} {
		incr argi
		set port [lindex $argv $argi]
	} elseif {$arg == "-dbPrefix"} {
		incr argi
		set prefix [lindex $argv $argi]
	} elseif {$arg == "-dbHost" } {
		incr argi
		set db_host [lindex $argv $argi]
	} elseif {$arg == "-dbPort" } {
		incr argi
		set db_port [lindex $argv $argi]
	} elseif {$arg == "-dbSid"} {
		incr argi
		set db_sid [lindex $argv $argi]
	} elseif {$arg == "-dbPassword"} {
		incr argi
		set db_password [lindex $argv $argi]
	} elseif {$arg == "-sslEnabled"} {
		incr argi
		set ssl_enabled [string tolower [lindex $argv $argi]]
	} elseif {$arg == "-sslPort"} {
		incr argi
		set ssl_port [lindex $argv $argi]
	} elseif {$arg == "-soaOH"} {
		incr argi
		set soa_oh [lindex $argv $argi]
	} elseif {$arg == "-bpm"} {
		incr argi
		set bpm [string tolower [lindex $argv $argi]]
	} elseif {$arg == "-osb"} {
		incr argi
		set osb [string tolower [lindex $argv $argi]]
	}

	incr argi;
}

puts "SOA Oracle Home = $soa_oh"
puts "Domain name = $domain_name"
puts "Domain dir = $domain_dir"
puts "Apps dir = $app_dir"
puts "Domain port = $port"
# puts "Domain password = $password"
puts "DB prefix = $prefix"
puts "DB host = $db_host"
puts "DB port = $db_port"
puts "DB SID = $db_sid"
# puts "DB password = $db_password"
puts "SSL enabled = $ssl_enabled"
if {$ssl_enabled == "true"} {
	puts "SSL port = $ssl_port"
}
puts "BPM included = $bpm"
puts "OSB included = $osb"

set i 0
set j 0
set k 0
set k2 0
set l 0
set m 0; set mm 0
set n 0
set p 0
set ds 1

# proc sl {} {sleep 5}

if {[file isdirectory $soa_oh] == 0} {
	puts "ERROR: Missing or Invalid SOA Oracle Home";
	usage;
	exit -1;
}

if { $domain_name == "" || $domain_dir == "" || $password == "" } {
	puts "ERROR: Invalid domain name and/or domain dir and/or domain password"
	usage;
	exit -1;
}

if { $prefix == "" || $db_host == "" || $db_port == "" || $db_sid == "" || $db_password == "" } {
	puts "ERROR: Invalid database prefix, host, port, sid, or password"
	usage;
	exit -1;
}

