ns7:component-version-script.script:
#!/bin/bash
# Copyright (c) 2013 Trymaran Inc. All rights reserved.

set -e
test "$ENABLED" != "true" && exit 0;

case "$JDK_INSTALLER" in
	*.bin)	cd "$INSTALL_DIR";
		if [ "$OS_USER" != "root" ]; then
			su $OS_USER -c "/bin/sh $JDK_INSTALLER"
		else
			/bin/sh $JDK_INSTALLER
		fi;
		;;

	*.rpm)
		rpm -ivh "$JDK_INSTALLER"
		;;
esac;

if [ "$DELETE_CONTENT" = "true" ]; then
	rm -f $JDK_INSTALLER
fi;

supported-os-details.exec-script:
trap "APPDEXITSTATUS=\$?;printenv > $2;exit $APPDEXITSTATUS" EXIT #Start of Json Parser
